function onEntry(entry){
	entry.forEach(change =>  {
		if(change.isIntersecting) {
			change.target.classList.add("paje3__show")
		}
	});
}
let options = {
	threshold: [0.5]};

let observer = new IntersectionObserver(onEntry, options);
let observer2 = new IntersectionObserver(onEntry, options);


let elements = document.querySelectorAll(".paje4")
let elements4 = document.querySelectorAll(".paje5")
let elements5 = document.querySelectorAll(".paje6")
let elements6 = document.querySelectorAll(".paje7")
let elements7 = document.querySelectorAll(".paje8")


for(let elm of elements) {
	observer.observe(elm);
}
for(let elm2 of elements4) {
	observer2.observe(elm2);
}
for(let elm2 of elements5) {
	observer2.observe(elm2);
}
for(let elm2 of elements6) {
	observer2.observe(elm2);
}
for(let elm2 of elements7) {
	observer2.observe(elm2);
}


